mapper.select:
SELECT A.*,B.totalCnt FROM ( SELECT tt1.id, tt1.pw, tt1.nickName, tt1.name, tt1.phone, tt1.email, DECODE(TO_CHAR(tt1.reg_dt,'YYYYMMDD'), TO_CHAR(SYSDATE,'YYYYMMDD') ,TO_CHAR(tt1.reg_dt,'HH24:MI') ,TO_CHAR(tt1.reg_dt,'YYYY-MM-DD')) as regDt FROM ( SELECT ROWNUM rnum, T1.* FROM ( SELECT * FROM users --WHERE 검색 WHERE 1=1
ORDER BY reg_dt DESC )T1 --10,20,30,... WHERE ROWNUM <=#{pageSize} * (#{pageNo}-1) + #{pageSize} )TT1 --1,11,21,31,.. WHERE rnum >= #{pageSize} * (#{pageNo}-1) + 1 )A CROSS JOIN ( SELECT COUNT(*) totalCnt FROM users --WHERE 검색 WHERE 1=1
)B

mapper.update:
UPDATE users
SET nickName = #{nickName},
	phone = #{phone},
    email = #{email},
    name = #{name}
WHERE id = #{id}
